import styled, {css} from "styled-components";

export const Container = styled.div`
  display: flex;
  flex: 1;
  height: 80px;
  justify-content: flex-start;
  align-items: center;
  padding-left: 56px;
  background-color: ${({ theme }) => theme.white};

  @media (max-width: 500px)
  {
    background-color: red;
    height: 56px;
    padding-left: 16px;
  }
`;
