import styled from "styled-components";

export const Button = styled.button`
  padding: 18px;
  background-color: ${({ theme }) => theme.colors.brandColorPrimary};
  border: 0;
  border-radius: 32px;
  color: ${({ theme }) => theme.colors.white};
  font-size: 16px;
  line-height: 20px;
  font-weight: 600;
  cursor: pointer;

  :hover {
    opacity .9;
  }
`;
