import React from "react";
import { ReactComponent as LeftArrow } from "../../assets/arrow-left.svg";
import { ReactComponent as RightArrow } from "../../assets/arrow-right.svg";
import * as S from "./reachDatePicker.styled";

interface ReachDatePickerProps {
  current: number;
  prevClick: () => void;
  nextClick: () => void;
}

const MONTH_LIST = [
  "Jan",
  "Feb",
  "Mar",
  "Apr",
  "May",
  "Jun",
  "Jul",
  "Aug",
  "Sep",
  "Oct",
  "Nov",
  "Dec",
];

const ReachDatePicker = ({
  current,
  prevClick,
  nextClick,
}: ReachDatePickerProps) => {
  console.log("ðŸš€ ~ file: index.tsx ~ line 33 ~ current", current);
  const todayMonth = new Date().getMonth();
  console.log("ðŸš€ ~ file: index.tsx ~ line 33 ~ todayMonth", todayMonth);

  return (
    <S.Container>
      <S.ArrowButton onClick={prevClick} disabled={current <= 1}>
        <LeftArrow />
      </S.ArrowButton>
      <S.DateView>
        <strong>{MONTH_LIST[todayMonth + current]}</strong>
        <p>2021</p>
      </S.DateView>
      <S.ArrowButton onClick={nextClick}>
        <RightArrow />
      </S.ArrowButton>
    </S.Container>
  );
};

export default ReachDatePicker;
