import styled from "styled-components";

export const Container = styled.div`
  padding: 40px;
  border-radius: 8px;
  width: 560px;
  background-color: ${({ theme }) => theme.colors.white};
  border: 1px solid black;

  @media (max-width: 500px)
  {
    width: 500px;
  }
`;
