/* eslint-disable no-console */
import React from "react";

import AmountInput from "../AmountInput";

import { ReactComponent as House } from "../../assets/icons/buy-a-house.svg";
import * as S from "./card.styled";

const Card = () => {
  const [monthlyAmount, setMonthlyAmount] = useState(0);

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>): void => {
    e.preventDefault();
    const amount = document.getElementById("amount") as HTMLInputElement;
    const reachDate = document.getElementById("reachDate") as HTMLInputElement;
    console.log(
      "ðŸš€ ~ file: index.tsx ~ line 12 ~ handleSubmit ~ amount",
      amount.value,
      reachDate.value,
      Number(amount.value) / Number(reachDate.value)
    );
  };

  return (
    <S.Container>
      <S.HeadSection>
        <S.Icon>
          <House />
        </S.Icon>
        <S.HeadTextSection>
          <h2>Buy a house</h2>
          <h3>Saving goal</h3>
        </S.HeadTextSection>
      </S.HeadSection>

      <form onSubmit={handleSubmit}>
        <S.InputSection>
          <S.InputField>
            <S.InputLabel>Total amount</S.InputLabel>
            <AmountInput />
          </S.InputField>
          <S.InputField>
            <S.InputLabel>Reach goal by</S.InputLabel>
            <S.Input id="reachDate" />
          </S.InputField>
        </S.InputSection>

        <S.MonthlyAmountInfo>
          <S.MonthlyAmountValues>
            <h2>Monthly amount</h2>
            <strong title={`$${monthlyAmount}`}>{`$${monthlyAmount}`}</strong>
          </S.MonthlyAmountValues>
          <S.MonthlyAmountDescription>
            <p>
              Youâ€™re planning <strong>48 monthly deposits</strong> to reach your{" "}
              <strong>$25,000</strong> goal by <strong>October 2020</strong>.
            </p>
          </S.MonthlyAmountDescription>
        </S.MonthlyAmountInfo>

        <S.ConfirmButton>Confirm</S.ConfirmButton>
      </form>
    </S.Container>
  );
};

export default Card;
