import React from "react";
import Header from "../Header";
import Card from "../Card";

import * as S from "./home.styled";

const format = (input: string) =>
  input.replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");

const Home = () => (
  <S.Container>
    <Header />
    <S.SubtitleView>
      <span>
        {/* Let&apos;s plan your <strong>saving goal</strong>. */}
        {format("1000000.99")}
      </span>
    </S.SubtitleView>
    <S.ContentView>
      <Card />
    </S.ContentView>
  </S.Container>
);

export default Home;
