import React from "react";

const format = (input: string) =>
  input.replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");

const AmountInput = () => {
  return <input type="number" name="amount" data-mask="000.000" />;
};

export default AmountInput;
