  };

  return (
    <S.Container>
      <S.HeadSection>
        <S.Icon>
          <House />
        </S.Icon>
        <S.HeadTextSection>
          <h2>Buy a house</h2>
          <h3>Saving goal</h3>
        </S.HeadTextSection>
      </S.HeadSection>

      <form onSubmit={handleSubmit}>
        <S.InputSection>
          <S.InputField>
            <S.InputLabel>Total amount</S.InputLabel>
            <AmountInput />
          </S.InputField>
          <S.InputField>
            <S.InputLabel>Reach goal by</S.InputLabel>
            <DatePicker
              selected={startDate}
              onChange={(date: Date) => setStartDate(date)}
              dateFormat="MMMM yyyy"
              showMonthYearPicker
