              disabledKeyboardNavigation={false}
            />
          </S.InputField>
        </S.InputSection>

        <S.MonthlyAmountInfo>
          <S.MonthlyAmountValues>
            <h2>Monthly amount</h2>
            <strong title={`$${monthlyAmount}`}>{`$${monthlyAmount}`}</strong>
          </S.MonthlyAmountValues>
          <S.MonthlyAmountDescription>
            <p>
